import React from 'react';
import styled from 'styled-components';
import {
    Button,
    Rating,
    Tag,
    Thumbnail,
} from '../ui';

const ListItemContainer = styled.div`
    align-items: center;
    border-bottom: 1px solid #eee;
    box-sizing: border-box;
    display: flex;
    flex-direction: row;
    padding: 16px;
    width: 100%;
`;

const ThumbnailWrap = styled.div`
    flex: 2;
`;

const DetailsSection = styled.div`
    flex: 8;
`;

const MainDetail = styled.div`
    font-weight: bold;
    font-size: 20px;
`;

const ButtonWrap = styled.div`
    flex: 1;
`;

const TagSection = styled.div`
    margin: 16px 0;
`;

/*
    This component displays the basic details for a given restaurant
    in the results section of the Search page.
*/
export const RestaurantListItem = ({ item: restaurant, onSelect }) => {
    const { name, rating, address, tags = [] } = restaurant;
    return (
        <ListItemContainer>
            <ThumbnailWrap>
                <Thumbnail width='100px' height='100px' url={restaurant.imageUrl} />
            </ThumbnailWrap>
            <DetailsSection>
                <MainDetail>{name}</MainDetail>
                <Rating
                    value={rating}
                    readOnly />
                <div>
                    {address.street},&nbsp;
                    {address.city}&nbsp;
                </div>
                <TagSection>
                    {tags.map(tag => <Tag key={tag}>{tag}</Tag>)}
                </TagSection>
            </DetailsSection>
            <ButtonWrap>
                <Button
                    type='shadow'
                    onClick={() => onSelect(restaurant)}
                >View</Button>
            </ButtonWrap>
        </ListItemContainer>
    );
}
